package Practice;

public class loops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for (int i = 0; i <= 6 ; i++) {
            // start	stop   increment by 1
       System.out.println("Hello World " + i);
          }
		for (int i = 6; i > 0 ; i--) {
            // start	stop   increment by 1
       System.out.println("Hello World " + i);
          }
		 int z = 8;
		 while(z > 0) {
			 System.out.println("Hello World awhile " + z);
			 z--;
		 }
        }

}
